import React from "react";
import "./App.css";
import axios from "axios";

class ContactUs extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      email: "",
      message: "",
      emailStatus: "",
    };
  }
  render() {
    return (
      <div className="App">
        <form>
          <label>
            <input
              type="text"
              placeholder="Name"
              onChange={this.handleChange("name")}
            />
          </label>
          <label>
            <input
              type="text"
              placeholder="Email"
              onChange={this.handleChange("email")}
            />
          </label>
          <label>
            <input
              type="text"
              placeholder="Message"
              onChange={this.handleChange("message")}
            />
          </label>
          <button type="submit"></button>
        </form>
      </div>
    );
  }

  onNameChange(event) {
    this.setState({ name: event.target.value });
  }

  onEmailChange(event) {
    this.setState({ email: event.target.value });
  }

  onMessageChange(event) {
    this.setState({ message: event.target.value });
  }
}

function App() {
  return (
    <div className="App">
      <h1>Pandan Cake!</h1>
      <div>
        <ContactUs />
      </div>
    </div>
  );
}

export default App;
